INSERT INTO `question` (`QuestionID`, `STT`, `IDBank`, `Title`, `Ans1`, `Ans2`, `Ans3`, `Ans4`, `CorrectAns`, `Comment`, `TimeCreated`, `UserCreated`) VALUES
(23, 1, 12, 'Đại hội nào sau đây của Đảng đánh dấu mốc quan trọng trong nhận thức về tư tưởng Hồ  Chí Minh?', 'Đại hội VII (1991).', 'Đại hội VIII (1996).', 'Đại hội IX (2001).', 'Đại hội X (2006).', 1, '', '2025-07-14 20:03:24', 1),
(24, 2, 12, 'Khái niệm có tính toàn diện và hệ thống về tư tưởng Hồ Chí Minh được Đại hội nào sau  đây của Đảng đã khẳng định?', 'Đại hội X (2006).', 'Đại hội XI (2011).', 'Đại hội XII (2016).', 'Đại hội XIII (2021).', 2, '', '2025-07-14 20:07:36', 1),
(25, 3, 12, 'Tư tưởng Hồ Chí Minh là tài sản tinh thần vô cùng to lớn và quý giá của Đảng và dân tộc ta, mãi mãi soi đường cho sự nghiệp cách mạng của nhân dân ta được khẳng định tại Đại hội nào sau đây của Đảng?', 'Đại hội IX (2001).', 'Đại hội X (2006).', 'Đại hội XI (2011).', 'Đại hội XII (2016).', 3, '', '2025-07-14 20:13:55', 1),
(26, 4, 12, 'Khái niệm tư tưởng Hồ Chí Minh cho thấy tư tưởng Hồ Chí Minh được hình thành từ', 'thực tiễn Việt Nam và thế giới cuối thế kỷ XIX đầu thế kỷ XX.', 'sự thành công vang dội của cách mạng Tháng Mười Nga.', 'tài năng hoạt động, tổng kết thực tiễn,  phát triển lý luận của Hồ Chí Minh.', 'chủ nghĩa Mác - Lênin, giá trị truyền thống dân tộc, tinh  hoa văn hóa nhân loại.', 4, '', '2025-07-14 20:14:23', 1),
(31, 5, 12, 'Tư tưởng Hồ Chí Minh là kết quả của sự vận dụng và phát triển sáng tạo chủ nghĩa Mác - Lênin vào', 'điều kiện cụ thể của nước ta.', 'hoàn cảnh lịch sử từng nước.', 'phong trào giải phóng dân tộc.', 'đầu thế kỷ XX.', 1, '', '2025-07-15 18:46:21', 3),
(32, 6, 12, 'Nghị quyết Đại hội đồng UNESCO lần thứ 24 vinh danh Hồ Chí Minh là anh hùng giải phóng dân tộc, nhà', 'tư tưởng lớn của Việt Nam.', 'cách mạng chân chính của Việt Nam.', 'văn hóa kiệt xuất của Việt Nam.', 'chính trị lỗi lạc của Việt Nam.', 3, '', '2025-07-15 18:47:29', 3),
(33, 7, 12, 'Tư tưởng Hồ Chí Minh là một hệ thống quan điểm toàn diện và sâu sắc về những vấn đề', 'cấp bách của cách mạng Việt Nam.', 'cơ bản của cách mạng Việt Nam.', 'cấp bách của cách mạng dân tộc dân chủ nhân dân.', 'cơ bản của cách mạng dân tộc dân chủ nhân dân', 2, '', '2025-07-15 19:06:03', 3),
(34, 8, 12, 'Đại hội nào sau đây của Đảng yêu cầu: “Toàn Đảng hãy ra sức học tập đường lối chính trị, tác phong và đạo đức cách mạng của Hồ Chủ tịch”?', 'Đại hội II (1951)', 'Đại hội VII (1991)', 'Đại hội IX (2001).', 'Đại hội XI (2011).', 1, '', '2025-07-15 19:06:36', 3),
(35, 9, 12, 'Luận điểm: “Đảng lấy chủ nghĩa Mác - Lênin và tư tưởng Hồ Chí Minh làm nền tảng tư tưởng, kim chỉ nam cho hành động” được nêu ra tại Đại hội nào sau đây?', 'Đại hội II (1951).', 'Đại hội VII (1991).', 'Đại hội IX (2001).', 'Đại hội XI (2011).', 2, '', '2025-07-15 19:10:26', 3),
(36, 10, 12, 'Nội dung nào sau đây không phải là đối tượng nghiên cứu của môn học Tư tưởng Hồ Chí Minh?', 'Tư tưởng Hồ Chí Minh về Đảng Cộng sản Việt Nam.', 'Quan điểm của Hồ Chí Minh về con đường cách mạng Việt Nam.', 'Tư tưởng Hồ Chí Minh về tập thể dục.', 'Quan điểm của Hồ Chí Minh về dân tộc thiểu số.', 3, '', '2025-07-15 19:10:58', 3),
(37, 11, 12, 'Luận điểm của Hồ Chí Minh: \"Lý luận cũng như cái tên, thực hành cũng như cái đích để bắn. Có tên mà không bắn, hoặc bắn lung tung cũng như không có tên\" thể hiện phương pháp luận nào sau đây?', 'Thống nhất tính đảng và tính khoa học.', 'Thống nhất lý luận và thực tiễn.', 'Quan điểm toàn diện và hệ thống.', 'Quan điểm lịch sử - cụ thể.', 2, '', '2025-07-15 19:11:43', 3),
(38, 12, 12, 'Khi nghiên cứu tư tưởng Hồ Chí Minh cần phải đứng trên quan điểm của chủ nghĩa Mác - Lênin, đường lối của Đảng đồng thời phải có tính khách quan nhằm đảm bảo nguyên tắc', 'thống nhất lý luận và thực tiễn.', 'quan điểm toàn diện và hệ thống.', 'quan điểm kế thừa và phát triển.', 'thống nhất tính đảng và tính khoa học.', 4, '', '2025-07-15 19:12:16', 3),
(39, 13, 12, 'Luận điểm của Hồ Chí Minh: \"Học mà không hành thì học vô ích. Hành mà không học thì hành không trôi chảy” thể hiện phương pháp luận nào sau đây?', 'Thống nhất tính đảng và tính khoa học.', 'Quan điểm kế thừa và phát triển.', 'Thống nhất lý luận và thực tiễn.', 'Quan điểm lịch sử - cụ thể.', 3, '', '2025-07-15 19:12:48', 3),
(40, 14, 12, 'Nội dung nào sau đây không phải là ý nghĩa của việc học tập môn Tư tưởng Hồ Chí Minh?', 'Góp phần nâng cao năng lực tư duy lý luận.', 'Đưa cách mạng Việt Nam đến thắng lợi', 'Giáo dục và thực hành đạo đức cách mạng.', 'Rèn luyện phương pháp và phong cách công tác.', 2, '', '2025-07-15 19:14:05', 3),
(41, 15, 12, 'Ý nghĩa của việc học tập, nghiên cứu môn Tư tưởng Hồ Chí Minh là', 'góp phần vào cuộc đấu tranh vì độc lập, dân chủ, hòa bình trên thế giới.', 'đưa cách mạng giải phóng dân tộc Việt Nam đến thắng lợi', 'mở ra cho các dân tộc thuộc địa con đường giải phóng dân tộc gắn với sự tiến bộ xã hội.', 'giáo dục và thực hành đạo đức, củng cố niềm tin khoa học, trau dồi tình cảm cách mạng.', 4, '', '2025-07-15 19:15:13', 3),
(42, 16, 12, 'Khi nghiên cứu tư tưởng Hồ Chí Minh cần sử dụng phương pháp liên ngành vì', 'Hồ Chí Minh để lại một khối lượng bài nói, bài viết, tác phẩm rất lớn.', 'Hồ Chí Minh để lại nhiều tác phẩm, bài nói, bài viết với nội dung phong phú.', 'tư tưởng Hồ Chí Minh là sự vận dụng sáng tạo và phát triển chủ nghĩa Mác - Lênin vào Việt Nam', 'tư tưởng Hồ Chí Minh bao gồm nhiều lĩnh vực: chính trị, triết học, kinh tế, quân sự, văn hóa…', 4, '', '2025-07-15 19:16:56', 3),
(43, 17, 12, 'Việc chia quá trình hình thành và phát triển tư tưởng Hồ Chí Minh thành 5 thời kỳ thể hiện phương pháp cụ thể nào sau đây trong nghiên cứu tư tưởng Hồ Chí Minh?', 'Chuyên ngành, liên ngành.', 'Tiếp xúc nhân chứng lịch sử.', 'Khảo sát điền dã.', 'Thống nhất lịch sử và logic.', 4, '', '2025-07-15 19:18:04', 3),
(44, 18, 12, 'Sinh viên tra cứu bộ sách Hồ Chí Minh toàn tập là biểu hiện của việc sử dụng phương pháp cụ thể nào sau đây trong nghiên cứu tư tưởng Hồ Chí Minh?', 'Thống nhất lịch sử và logic.', 'Chuyên ngành, liên ngành.', 'Phân tích văn bản.', 'Điều tra xã hội học.', 3, '', '2025-07-15 19:18:41', 3),
(45, 19, 12, 'Hiện đại hóa tư tưởng Hồ Chí Minh là biểu hiện của sự vận dụng không đúng phương pháp luận nào sau đây?', 'Thống nhất tính đảng và tính khoa học.', 'Quan điểm kế thừa và phát triển', 'Thống nhất lý luận và thực tiễn.', 'Quan điểm lịch sử - cụ thể.', 4, '', '2025-07-15 19:20:41', 3),
(46, 20, 12, 'Luận điểm của Hồ Chí Minh: “Có kinh nghiệm mà không có lý luận, cũng như một mắt sáng, một mắt mờ” chỉ rõ', 'tầm quan trọng của kinh nghiệm.', 'tầm quan trọng của thực tế.', 'vai trò quan trọng của thực tiễn trong mối quan hệ với lý  luận.', 'vai trò quan trọng của lý luận trong mối quan hệ với thực tiễn.', 4, '', '2025-07-15 19:21:12', 3),
(47, 21, 12, 'Theo Hồ Chí Minh, cuộc cách mạng đã làm “thức tỉnh các dân tộc châu Á” là', 'Cách mạng tư sản Pháp (1789).', 'Cách mạng tư sản Mỹ (1776).', 'Cách mạng Tháng Mười Nga (1917).', 'Cách mạng Tân Hợi ở Trung Quốc (1911).', 3, '', '2025-07-15 19:23:05', 3),
(48, 22, 12, 'Việc hình thành tư tưởng Hồ Chí Minh không chịu tác động của sự kiện lịch sử cuối thế kỷ XIX, đầu thế kỷ XX nào sau đây?', 'Chủ nghĩa tư bản từ giai đoạn tự do cạnh tranh chuyển sang độc quyền.', 'Thắng lợi của Cách mạng Tháng Mười Nga.', 'Sự ra đời của Quốc tế Cộng sản.', 'Chiến tranh Thế giới lần thứ hai.', 4, '', '2025-07-15 19:23:33', 3),
