(382, 342, 19, 'Từ, cụm từ nào sau đây không nằm trong quan niệm Hồ Chí Minh về vai trò và sức mạnh của đạo đức?', 'Cái gốc.', 'Nền tảng.', 'Gánh được nặng.', 'Đi được nhanh.', 4, '', '2025-07-16 02:05:32', 2),
(383, 343, 19, 'Theo tư tưởng Hồ Chí Minh, vai trò của đạo đức còn thể hiện ở chỗ là', 'tiêu chí đánh giá phẩm chất người  cách mạng.', 'thước đo lòng cao thượng của con  người.', 'nhân tố quyết định bản chất cách mạng.', 'phương tiện để đi đến thành công.', 2, '', '2025-07-16 02:06:24', 2),
(384, 344, 19, 'Trong di chúc, Hồ Chí Minh căn dặn phải  đào tạo thế hệ trẻ thành những người', 'vừa hồng, vừa  chuyên', 'vừa có tâm, vừa có  tầm.', 'trung với nước, hiếu  với dân.', 'trung với Đảng, hiếu  với dân.', 1, '', '2025-07-16 02:06:48', 2),
(385, 345, 19, 'trung với Đảng, hiếu  với dân.', 'trung với Đảng, hiếu  với dân.', 'Thương đến cả kẻ thù của mình.', 'Thương yêu những người bị áp bức.', 'Hiếu với cha mẹ người khác.', 2, '', '2025-07-16 02:07:21', 2),
(386, 346, 19, 'Theo tư tưởng Hồ Chí Minh, biểu hiện nào sau đây không phải là đạo đức của người cách mạng?', 'Trung với nước, trung với Đảng.', 'Hiếu với dân, hiếu với cha mẹ.', 'Yêu thương con người không phân biệt màu da, chủng  tộc.', 'Yêu thương con người không phân biệt giai cấp.', 2, '', '2025-07-16 02:07:55', 2),
(387, 347, 19, 'Theo tư tưởng đạo đức Hồ Chí Minh, làm việc có kế hoạch thuộc nội hàm khái niệm  nào sau đây?', 'Cần.', 'Kiệm.', 'Liêm.', 'Chính.', 1, '', '2025-07-16 02:08:14', 2),
(388, 348, 19, 'Theo tư tưởng đạo đức Hồ Chí Minh, ham học, ham làm, ham tiến bộ thuộc nội hàm  khái niệm nào sau đây?', 'Cần.', 'Kiệm.', 'Liêm.', 'Chính.', 2, '', '2025-07-16 02:08:33', 2),
(389, 349, 19, 'Theo tư tưởng đạo đức Hồ Chí Minh, \"không tà\" thuộc nội hàm khái niệm nào sau  đây?', 'Cần.', 'Kiệm.', 'Liêm.', 'Chính.', 3, '', '2025-07-16 02:09:05', 2),
