(504, 432, 21, 'Quan điểm nào sau đây về đường lối đối ngoại được nêu ra ở Đại hội XIII của Đảng  thể hiện sự vận dụng tư tưởng đoàn kết quốc tế của Hồ Chí Minh?', 'Muốn là bạn.', 'Sẵn sàng là bạn.', 'Là bạn và đối tác tin cậy.', 'Là bạn, là đối tác tin cậy.', 4, '', '2025-07-17 00:44:49', 2),
(505, 433, 21, 'Nội dung nào sau đây không phải là một trong 5 điểm lớn của việc xây dựng nền văn hóa dân tộc theo quan điểm Hồ Chí Minh?', 'Xây dựng tâm lý.', 'Xây dựng luân lý.', 'Xây dựng văn hóa.', 'Xây dựng chính trị.', 3, '', '2025-07-17 00:45:33', 2),
(506, 434, 21, 'Hiện tượng mê tín dị đoan không thể hiện được tính chất nào sau đây của nền văn hóa mới Việt Nam theo tư tưởng Hồ Chí Minh?', 'Dân tộc.', 'Khoa học.', 'Cách mạng.', 'Đại chúng.', 2, '', '2025-07-17 00:46:00', 2),
(507, 435, 21, 'Theo tư tưởng đạo đức Hồ Chí Minh, làm việc phải tính đến hiệu quả lao động thuộc nội hàm khái niệm nào sau đây?', 'Cần.', 'Kiệm.', 'Liêm.', 'Chính.', 1, '', '2025-07-17 00:46:17', 2),
(508, 436, 21, 'Nội dung nào sau đây không phải là một trong năm điều Bác Hồ dạy thiếu niên, nhi đồng được Người viết trong Thư gửi thiếu niên, nhi đồng toàn quốc nhân dịp kỷ niệm 20 năm ngày thành lập Đội Thiếu niên tiền phong?', 'Yêu Tổ quốc, yêu đồng bào.', 'Học tập tốt, lao động tốt.', 'Giữ gìn vệ sinh.', 'Khiêm tốn, thật thà, dũng cảm.', 4, '', '2025-07-17 00:46:33', 2),
(509, 437, 21, 'Sinh viên đi học đầy đủ nhưng lên lớp không tập trung nghe giảng là không thực hiện được phẩm chất nào sau đây theo tư tưởng đạo đức Hồ Chí Minh?', 'Cần.', 'Kiệm.', 'Liêm.', 'Chính.', 1, '', '2025-07-17 00:46:52', 2),
(510, 438, 21, 'Cương lĩnh xây dựng đất nước trong thời kỳ quá độ lên chủ nghĩa xã hội (bổ sung, phát triển năm 2011) khẳng định phải xây dựng nền văn hóa Việt Nam', 'tiên tiến, đậm đà bản sắc dân tộc, phát  triển toàn diện, thống nhất trong đa dạng.', 'hiện đại, đậm đà bản sắc dân tộc, phát  triển toàn diện, thống nhất trong đa dạng.', 'tiên tiến, thấm nhuần sâu sắc tinh thần cách mạng.', 'hiện đại, thấm nhuần sâu sắc tinh thần nhân đạo.', 1, '', '2025-07-17 00:47:12', 2),
(511, 439, 21, 'Một trong những nội dung học tập đạo đức cách mạng Hồ Chí Minh là phải', 'trung với nước, hiếu với dân, suốt đời đấu tranh cho lợi ích của Đảng.', 'trung với nước, hiếu với dân, suốt đời đấu tranh cho sự nghiệp cách mạng.', 'trung với đảng, hiếu với dân, suốt đời đấu tranh cho sự nghiệp cách mạng.', 'trung với đảng, hiếu với dân, suốt đời đấu tranh cho lợi ích của đảng.', 2, '', '2025-07-17 00:49:00', 2),
(512, 440, 21, 'Nói chuyện tại Đại hội sinh viên Việt Nam lần thứ II (1958), Hồ Chí Minh đã dùng hình ảnh nào sau đây để nói về người có tài mà không có đức?', 'Ông Bụt.', 'Người thụt két.', 'Người đi đêm.', 'Ông Tiên.', 2, '', '2025-07-17 00:49:17', 2),
(513, 441, 21, 'Đại hội nào sau đây của Đảng đã nhấn mạnh: “Đảng phải đặc biệt coi trọng việc tổ chức học tập một cách có hệ thống tư tưởng, đạo đức, tác phong của Chủ tịch Hồ Chí Minh trong toàn Đảng”?', 'Đại hội V (1982).', 'Đại hội IX (2001).', 'Đại hội XI (2011).', 'Đại hội XIII (2021).', 1, '', '2025-07-17 00:49:40', 2),
(514, 442, 21, 'Việc học tập môn Tư tưởng Hồ Chí Minh  không mang lại ý nghĩa nào sau đây?', 'Góp phần mở ra con đường giải phóng cho	các	dân	tộc thuộc địa.', 'Góp phần nâng cao năng lực tư duy lý luận.', 'Giáo dục và thực hành đạo đức cách mạng.', 'Giáo dục và rèn luyện phương pháp và phong cách công tác.', 1, '', '2025-07-17 00:50:00', 2),
(515, 443, 21, 'Phong trào Cần Vương, một trong những cơ sở thực tiễn hình thành tư tưởng Hồ Chí Minh, do giai cấp, tầng lớp nào sau đây lãnh đạo?', 'Nông dân.', 'Công nhân.', 'Văn thân, sĩ phu.', 'Tư sản dân tộc.', 3, '', '2025-07-17 00:50:19', 2),
(516, 444, 21, 'Một trong những cơ sở lý luận hình thành tư tưởng Hồ Chí Minh là', 'chủ nghĩa tam dân', 'chủ nghĩa hiện sinh.', 'thuyết ba đại diện.', 'triết học duy lý.', 1, '', '2025-07-17 00:50:40', 2),
(517, 445, 21, 'Hồ Chí Minh tiếp thu phương châm \"Cầu  đồng tồn dị\" từ', 'Nho giáo.', 'Phật giáo.', 'Lão giáo.', 'Thiên Chúa giáo.', 2, '', '2025-07-17 00:51:23', 2);
INSERT INTO `question` (`QuestionID`, `STT`, `IDBank`, `Title`, `Ans1`, `Ans2`, `Ans3`, `Ans4`, `CorrectAns`, `Comment`, `TimeCreated`, `UserCreated`) VALUES
(518, 446, 21, 'Tư tưởng coi trọng việc trồng cây của Hồ Chí Minh chịu ảnh hưởng bởi học thuyết nào sau đây?', 'Nho giáo.', 'Phật giáo.', 'Lão giáo.', 'Thiên Chúa giáo.', 3, '', '2025-07-17 00:51:39', 2),
(519, 447, 21, 'Luận cương của V.I.Lênin mà Nguyễn Ái Quốc đã đọc, được đăng trên tờ báo nào sau đây?', 'Người cùng khổ.', 'Nhân đạo.', 'Nhân dân.', 'Chuông rè.', 2, '', '2025-07-17 00:51:58', 2),
(520, 448, 21, 'Tư tưởng Hồ Chí Minh trở thành yếu tố chỉ đạo cách mạng Việt Nam từ', 'Hội nghị Trung ương 8 (5/1941).', 'Hội nghị Trung ương 7 (11/1940).', 'Đại hội VI (1986).', 'Đại hội VII (1991).', 4, '', '2025-07-17 00:52:23', 2),
(521, 449, 21, 'Nhân vật nào sau đây không phải là đồng  tác giả Bản Yêu sách của nhân dân An Nam mà Nguyễn Ái Quốc gửi đến Hội nghị Vécxây?', 'Phan Chu Trinh.', 'Phan Văn Trường.', 'Phan Bội Châu.', 'Nguyễn Tất Thành.', 3, '', '2025-07-17 00:52:48', 2),
(522, 450, 21, 'Tác phẩm, bài viết nào sau đây không liên quan đến Bản Yêu sách của nhân dân An Nam mà Nguyễn Ái Quốc đã gửi tới Hội nghị Vécxây?', 'An Nam chí lược.', 'Quyền của các dân tộc.', 'Việt Nam yêu cầu ca.', 'An Nam nhân dân thỉnh nguyện thư.', 1, '', '2025-07-17 00:54:14', 2),
(523, 481, 22, 'Nhận thức \"Đường lối chính trị, nền nếp làm việc và đạo đức cách mạng của Đảng ta hiện nay là đường lối, tác phong và đạo đức Hồ Chủ tịch\" thể hiện ở Đại hội nào sau đây của Đảng?', 'Đại hội II (1951).', 'Đại hội III (1960).', 'Đại hội IV (1976).', 'Đại hội XIII (2021).', 1, '', '2025-07-17 00:55:34', 2),
(524, 482, 22, 'Khi nghiên cứu tư tưởng Hồ Chí Minh phải quán triệt mối liên hệ qua lại giữa các thành tố, các bộ phận khác nhau trong cùng một tổng thể là đảm bảo nguyên tắc nào sau đây?', 'Thống nhất lý luận và thực tiễn.', 'Toàn diện và hệ thống.', 'Kế thừa và phát triển.', 'Thống nhất tính đảng và tính khoa học.', 2, '', '2025-07-17 00:55:55', 2),
(525, 483, 22, 'Một trong những điểm tích cực của Nho giáo mà Hồ Chí Minh đã tiếp thu là', 'vị tha, từ bi, bác ái, cứu khổ, cứu nạn.', 'khuyến học, chú trọng trồng người.', 'đạo đức, trong sạch, chăm làm việc thiện', 'yêu thiên nhiên, sống hòa mình với thiên  nhiên.', 2, '', '2025-07-17 00:57:04', 2),
(526, 484, 22, 'Một trong những điểm tích cực của Phật giáo mà Hồ Chí Minh đã tiếp thu là', 'tiết kiệm, khuyến học, đề cao đức trị.', 'đề cao lao động, chống lười biếng.', 'yêu thiên nhiên, sống hòa mình với thiên  nhiên', 'cẩn trọng sửa mình, lấy tu thân làm gốc.', 2, '', '2025-07-17 00:58:03', 2),
(527, 485, 22, 'Hồ Chí Minh nói về mình \"Riêng phần tôi thì làm một cái nhà nho nhỏ, nơi có non xanh, nước biếc để câu cá, trồng hoa (…) không dính líu gì với vòng danh lợi\" cho thấy tư tưởng Hồ Chí Minh có nguồn gốc từ', 'Nho giáo.', 'Phật giáo.', 'Lão giáo.', 'Thiên Chúa giáo.', 3, '', '2025-07-17 00:58:23', 2),
(528, 486, 22, 'Yếu tố nào sau đây giúp Hồ Chí Minh có khả năng tổng kết thực tiễn và dự báo  tương lai chính xác?', 'Được đào luyện trong môi trường Nho giáo', 'Sự chỉ dẫn của các nhà cách mạng tiền bối.', 'Vốn sống phong phú và sự trải nghiệm  thực tế sâu rộng.', 'Dự nhiều lớp đào tạo cán bộ do Quốc tế Cộng sản tổ chức.', 3, '', '2025-07-17 00:59:27', 2),
(529, 487, 22, 'Trong thời gian từ 1917 đến 1919, khi ở Pháp, Hồ Chí Minh đã tham gia', 'Đảng Xã hội Pháp.', 'Đảng Cộng sản pháp.', 'Hội Quốc liên.', 'Hội Liên hiệp thuộc địa.', 1, '', '2025-07-17 00:59:45', 2),
(530, 488, 22, 'Hồ Chí Minh quyết định đi theo con  đường cách mạng vô sản để giải phóng dân tộc khi đọc Luận cương của', 'C.Mác.', 'V.I.Lênin.', 'Ph.Ăngghen.', 'Tôn Trung Sơn.', 2, '', '2025-07-17 01:00:06', 2),
(531, 489, 22, 'Hội nghị Trung ương Đảng tháng 10/1930 đã phê phán vấn đề nào sau đây trong Cương lĩnh chính trị đầu tiên do Hồ Chí Minh soạn thảo?', 'Quá chú trọng vào việc đòi lợi ích giai cấp.', 'Không đề cập đến vấn đề đấu tranh giải phóng dân tộc.', 'Bỏ quên lợi ích giai  cấp để chỉ lo đến việc phản đế.', 'Chỉ tập trung vào cuộc đấu tranh giai cấp.', 3, '', '2025-07-17 01:01:06', 2),
(532, 490, 22, 'Sau khi thoát khỏi nhà tù của thực dân Anh ở Hồng Kông, Hồ Chí Minh đã', 'trở lại Liên Xô để học tập và nghiên cứu.', 'trở về Việt Nam tiếp tục lãnh đạo cách mạng.', 'tiếp tục hoạt động tại Hồng Kông.', 'sang Quảng Châu  (Trung Quốc) thành lập Hội Việt Nam cách mạng thanh niên.', 1, '', '2025-07-17 01:01:37', 2),
(533, 491, 22, 'Một trong những đóng góp quan trọng  của Hồ Chí Minh đối với sự phát triển và tiến bộ của nhân loại là', 'tập trung xây dựng liên minh quân sự giữa các nước nhỏ  yếu.', 'phá bỏ chính sách  \"chia để trị\" và thúc đẩy đoàn kết giữa các dân tộc.', 'kêu gọi các nước thuộc địa phát triển nền kinh tế tự do', 'từ chối mọi hình thức hợp tác quốc tế vì sợ phụ thuộc.', 2, '', '2025-07-17 01:01:57', 2),
(534, 492, 22, 'Đại hội nào sau đây của Đảng khẳng  định: Hồ Chí Minh là \"Người bạn thân thiết của các dân tộc đấu tranh vì hòa bình, độc lập dân tộc, dân chủ và tiến bộ xã hội trên toàn thế giới\"?', 'Đại hội X (2006).', 'Đại hội XI (2011).', 'Đại hội XII (2016).', 'Đại hội XIII (2021).', 4, '', '2025-07-17 01:02:21', 2),
(535, 493, 22, 'Câu \"Không có gì quý hơn độc lập, tự do\" được Hồ Chí Minh nói trong trường hợp nào sau đây?', 'Khi tuyên bố thành lập nước Việt Nam Dân  chủ  Cộng  hòa  (1945).', 'Trong Lời kêu gọi Toàn quốc kháng chiến (1946).', 'Trong Lời kêu gọi kháng chiến chống Mỹ cứu nước (1966).', 'Khi ký Hiệp định Paris về chấm dứt chiến tranh  ở  Việt  Nam  (1973).', 3, '', '2025-07-17 01:03:00', 2),
(536, 494, 22, 'Với câu “Dân tộc cách mệnh thì chưa phân giai cấp, nghĩa là sĩ, nông, công, thương đều nhất trí chống lại cường quyền”, Hồ Chí Minh nói về vấn đề nào sau đây?', 'Lực lượng cách mạng giải phóng dân tộc.', 'Đoàn kết dân tộc.', 'Đoàn kết giai cấp.', 'Lực lượng nòng cốt của cách mạng giải phóng dân tộc.', 3, '', '2025-07-17 01:03:19', 2),
(537, 495, 22, 'Theo tư tưởng Hồ Chí Minh, một trong những nguyên tắc xây dựng chủ nghĩa xã hội trong thời kỳ quá độ là', 'thống nhất mục tiêu và lợi ích.', 'làm tốt công tác vận động quần chúng.', 'kế thừa truyền thống yêu nước và đoàn kết dân tộc.', 'phải đoàn kết, học tập kinh nghiệm các nước anh em.', 4, '', '2025-07-17 01:03:39', 2),
(538, 496, 22, 'Theo tư tưởng Hồ Chí Minh, đặc điểm cơ bản của nền kinh tế thời kỳ quá độ ở Việt Nam là', 'tồn tại nhiều hình thức sở hữu và thành phần kinh tế đan xen.', 'nền kinh tế chỉ do Nhà nước quản lý và vận hành.', 'hoàn toàn dựa vào kinh tế hợp tác xã và doanh  nghiệp  nhà  nước.', 'bỏ qua sự phát triển của khu vực kinh tế tư nhân.', 1, '', '2025-07-17 01:03:58', 2),
(539, 497, 22, 'Khoản 1, Điều 91 Luật Đất đai 2024 quy định \"Việc bồi thường, hỗ trợ tái định cư khi nhà nước thu hồi đất phải đảm bảo dân chủ, khách quan, công bằng\" đã thể hiện nội dung nào sau đây trong tư tưởng Hồ Chí Minh về chủ nghĩa xã hội?', 'Mục tiêu về chính trị', 'Mục tiêu về kinh tế.', 'Nhiệm vụ về kinh tế', 'Nhiệm vụ về chính trị', 1, 'Tr 101', '2025-07-17 01:04:47', 2),
(540, 498, 22, 'Theo tư tưởng Hồ Chí Minh, để đảm bảo độc lập dân tộc gắn liền với chủ nghĩa xã hội thì cần có điều kiện nào sau đây?', 'Phát huy nhân tố con người trong sự nghiệp xây dựng chủ nghĩa  xã hội.', 'Phải đoàn kết, gắn bó chặt chẽ với cách mạng thế giới.', 'Xây dựng nền quốc phòng toàn dân vững mạnh, hiện đại,', 'Có đội ngũ cán bộ đủ đức đủ tài đáp ứng với yêu cầu của sự nghiệp cách mạng xã hội chủ nghĩa.', 2, '. Tr118', '2025-07-17 01:05:14', 2),
(541, 499, 22, 'Vận dụng tư tưởng Hồ Chí Minh trong giai đoạn hiện nay là phải chống lại  những suy thoái trong Đảng. Biểu hiện nào sau đây là sự suy thoái về đạo đức, lối sống được chỉ ra tại Nghị quyết Trung ương 4 khóa XII?', 'Kê khai tài sản, thu nhập không trung  thực.', 'Duy ý chí, áp đặt, bảo thủ, chỉ làm theo ý mình.', 'Trong tự phê bình  còn giấu giếm, không dám nhận khuyết  điểm.', 'Có tư tưởng dân tộc hẹp hòi, tôn giáo cực đoan.', 1, '', '2025-07-17 01:05:36', 2),
(542, 500, 22, 'Theo tư tưởng Hồ Chí Minh, nguyên tắc tổ chức cơ bản của Đảng Cộng sản Việt  Nam là', 'tập trung dân chủ.', 'tự phê bình và phê bình.', 'kỷ luật nghiêm minh', 'đoàn kết thống nhất trong Đảng.', 1, 'Tr130', '2025-07-17 01:06:04', 2),
(543, 521, 24, 'Biểu hiện nào sau đây thuộc về mặt văn minh trong quan niệm Đảng là đạo đức, là văn minh của Hồ Chí Minh?', 'Làm cho dân tộc được độc lập, nhân dân ấm no, tự do, hạnh phúc.', 'Trung thành với lợi ích của toàn dân tộc, không có lợi ích riêng.', 'Giàu sang không thể quyến rũ. Nghèo khó không thể chuyển lay.  Uy lực không thể khuất phục.', 'Giàu sang không thể quyến rũ. Nghèo khó không thể chuyển lay.  Uy lực không thể khuất phục.', 4, 'Tr129', '2025-07-20 01:10:23', 1),
(544, 522, 24, 'Trong tác phẩm Đường cách mệnh, Hồ Chí Minh dùng hình ảnh \"người cầm lái\"  để nói về', 'vai trò của Đảng.', 'bản chất của Đảng.', 'nền tảng tư tưởng của Đảng.', 'tính định hướng của Đảng.', 1, '', '2025-07-20 01:10:41', 1),
(545, 523, 24, 'Theo tư tưởng Hồ Chí Minh, nhà nước do nhân dân là nhà nước có biểu hiện nào sau đây?', 'Nhân dân được làm chủ mọi mặt xã hội.', 'Nhân dân có quyền bầu cử và ứng cử vào bộ máy nhà nước.', 'Nhà nước phục vụ và đảm bảo đời sống ấm no cho nhân dân.', 'Nhà nước đảm bảo quyền công dân.', 1, '', '2025-07-20 01:11:04', 1),
(546, 524, 24, 'Mục tiêu của Hồ Chí Minh khi xây dựng Nhà nước pháp quyền là', 'thiết lập một chính quyền tập trung quyền lực tuyệt đối vào một cá nhân.', 'tăng cường các biện pháp kiểm soát người dân bằng sắc lệnh.', 'xây dựng cơ chế quản lý xã hội dựa trên pháp luật và bảo vệ quyền lợi của nhân dân.', 'tạo dựng một nhà  nước quân sự hóa để đối phó với chiến tranh.', 3, '', '2025-07-20 01:11:36', 1),
(547, 525, 24, 'Ngày 03/09/1945, trong cuộc họp Hội đồng Chính phủ, Hồ Chí Minh đề nghị tổ chức một cuộc tổng tuyển cử nhằm mục đích', 'tranh thủ sự ủng hộ của các đảng phái trong nước.', 'thiết lập nền tảng pháp lý hợp hiến cho Nhà  nước Việt Nam mới.', 'đảm bảo quyền lực tuyệt đối của Chính phủ lâm thời.', 'tổ chức các cuộc đàm phán với thực dân Pháp.', 2, '', '2025-07-20 01:11:58', 1),
(548, 526, 24, 'Nhà nước ban hành Luật Bình đẳng giới (2006), Luật Hôn nhân và gia đình  (2014), Luật Trẻ em (2016) và các quy định về quyền phụ nữ, trẻ em trong hệ thống pháp luật nói chung đã thể hiện  được nội dung nào sau đây trong tư tưởng Hồ Chí Minh về nhà nước?', 'Dùng pháp luật quản lý xã hội.', 'Nhà nước hợp hiến, hợp pháp.', 'Thượng tôn pháp luật.', 'Pháp quyền nhân nghĩa.', 4, '', '2025-07-20 01:12:47', 1),
(549, 527, 24, 'Theo tư tưởng Hồ Chí Minh, pháp luật không chỉ là công cụ quản lý của nhà nước, mà còn là', 'công cụ để giám sát các cơ quan truyền thông.', 'cách thức mở rộng quan hệ quốc tế.', 'công cụ giúp người dân làm chủ và kiểm soát quyền lực nhà  nước.', 'cách thức xử lý hành vi tham nhũng của cán bộ, công chức.', 3, '', '2025-07-20 01:13:15', 1),
(550, 528, 24, 'Hồ Chí Minh thường nhấn mạnh đến  công tác chỉnh đốn Đảng trong bối cảnh nào sau đây?', 'Chuẩn bị đại hội Đảng.', 'Thời điểm mang tính bước ngoặt.', 'Thời điểm mang tính bước ngoặt.', 'Phong trào cộng sản quốc tế gặp khó khăn.', 1, '', '2025-07-20 01:13:37', 1),
(551, 529, 24, 'Trong tư tưởng Hồ Chí Minh, đại đoàn kết toàn dân tộc là mục tiêu và nhiệm vụ hàng đầu của', 'Đảng Cộng sản Việt Nam.', 'toàn dân.', 'Nhà nước.', 'Mặt trận dân tộc thống nhất.', 1, '', '2025-07-20 01:14:07', 1),
(552, 530, 24, 'Theo tư tưởng Hồ Chí Minh, trong các giai đoạn khác nhau, Mặt trận dân tộc  thống nhất có những tên gọi khác nhau vì lý do nào sau đây?', 'Để thích ứng với yêu cầu và nhiệm vụ của từng chặng đường.', 'Để phân biệt với tên gọi của các mặt trận khác trong khu vực.', 'Để phù hợp với tên gọi của Đảng theo từng thời kỳ.', 'Để giữ bí mật trong hoạt động.', 1, '', '2025-07-20 01:15:01', 1),
(553, 531, 24, 'Hồ Chí Minh cho rằng đoàn kết quốc tế không chỉ vì thắng lợi của mỗi quốc gia  mà còn vì', 'sự nghiệp chung của nhân loại tiến bộ.', 'lợi ích kinh tế của các quốc gia.', 'tăng cường vị thế quốc tế của Việt  Nam.', 'sự phát triển văn hóa toàn cầu.', 1, '', '2025-07-20 01:17:27', 1),
(554, 532, 24, 'Chủ trương đại đoàn kết toàn dân tộc  \"xóa bỏ mặc cảm, định kiến, phân biệt đối xử về quá khứ, thành phần, giai cấp…\"  của Đảng hiện nay thể hiện nội dung nào sau đây trong quan điểm Hồ Chí Minh về điều kiện xây dựng khối đoàn kết dân tộc?', 'Kế thừa truyền thống đoàn kết của dân tộc.', 'Phải có lòng khoan dung, độ lượng với con người.', 'Phải có niềm tin vào nhân dân.', 'Kế thừa truyền thống yêu nước và nhân ái của dân tộc.', 1, '', '2025-07-20 01:17:50', 1),
(555, 533, 24, 'Hồ Chí Minh cho rằng, trong đời sống, những yếu tố nào sau đây cần phải được coi là quan trọng ngang nhau?', 'Chính trị, kinh tế, văn hóa, xã hội.', 'Xã hội, giáo dục, y tế, kinh tế.', 'Văn hóa, chính trị, khoa học, thể thao.', 'Kinh tế, tôn giáo,  chính trị, nghệ thuật.', 1, '', '2025-07-20 01:18:14', 1),
(556, 534, 24, 'Những khái niệm mà tiếng Việt có từ để diễn đạt, nhưng lại dùng từ của tiếng  nước ngoài hoặc từ Hán Việt là trái với nội dung nào sau đây trong tư tưởng Hồ Chí Minh về văn hóa?', 'Xóa bỏ tàn tích văn hóa phong kiến.', 'Tiếp thu văn hóa nhân loại.', 'Tẩy trừ văn hóa thực dân.', 'Giữ gìn bản sắc văn hóa dân tộc.', 4, '', '2025-07-20 01:18:42', 1),
(557, 535, 24, 'Hồ Chí Minh dùng hình ảnh nào sau đây để ví với đạo đức cách mạng?', 'Gốc của cây và nguồn của sông suối.', 'Trí khôn của con  người và bàn chỉ nam của con tàu.', 'Ánh sáng của mặt trời.', 'Ngọn lửa bất diệt.', 1, '', '2025-07-20 01:19:12', 1),
(558, 536, 24, 'Theo tư tưởng Hồ Chí Minh, nguyên tắc xây dựng đạo đức cách mạng nào sau đây quan trọng nhất?', 'Nói đi đôi với làm.', 'Tu dưỡng đạo đức suốt đời', 'Lý luận gắn liền với thực tiễn.', 'Xây đi đôi với chống.', 1, '', '2025-07-20 01:19:35', 1),
(559, 537, 24, '\"Đạo đức cách mạng không phải trên trời sa xuống. Nó do đấu tranh, rèn luyện bền bỉ hàng ngày mà phát triển và củng cố. Cũng như ngọc càng mài càng sáng, vàng càng luyện càng trong\". Luận điểm trên của Hồ Chí Minh thể hiên nguyên tắc nào sau đây trong xây dựng đạo đức ?', 'Nói đi đôi với làm.', 'Xây đi đôi với chống.', 'Tu dưỡng suốt đời.', 'Nêu gương về đạo đức.', 3, 'tr 240', '2025-07-20 01:20:06', 1),
(560, 538, 24, 'Vận dụng tư tưởng Hồ Chí Minh, quan niệm \"Con người là trung tâm của chiến  lược phát triển\" được khẳng định trong tài liệu nào sau đây?', 'Cương lĩnh xây dựng đất nước thời kỳ quá độ (2011).', 'Nghị quyết Hội nghị Trung ương 9 khóa XI.', 'Hiến pháp 2013.', 'Nghị quyết Đại hội IX.', 1, 'tr 253', '2025-07-20 01:20:41', 1),
(561, 539, 24, 'Quan điểm nào sau đây không phù hợp với tư tưởng Hồ Chí Minh về phát triển văn hóa?', 'Văn hóa chỉ tập trung vào phục vụ cho mục tiêu giải trí của nhân dân.', 'Văn hóa là nền tảng tinh thần của xã hội, có vai trò quan trọng trong mọi lĩnh vực', 'Phát triển văn hóa là nhiệm vụ lâu dài và cần sự tham gia của toàn dân.', 'Phát triển văn hóa cần được xây dựng đồng bộ với chính trị và kinh tế.', 1, '', '2025-07-20 01:21:03', 1),
(562, 540, 24, 'Vận dụng tư tưởng Hồ Chí Minh về văn hóa, Đại hội XIII của Đảng đã xác định vấn đề nào sau đây đang ảnh hưởng tiêu cực đến môi trường văn hóa và xã hội?', 'Phát triển công nghệ thông tin.', 'Quản lý nhà nước quá nghiêm ngặt.', 'Tham nhũng và các tệ nạn xã hội.', 'Sự du nhập văn hóa ngoại lai.', 3, '', '2025-07-20 01:21:20', 1);
